mapper.select:
select s.id, s.category_id, s.name, s.price, s.status, s.description, s.image, s.update_time, c.name from setmeal as s left join category as c on s.category_id = c.id
and s.category_id = #{categoryId}
and s.name like concat('%', #{name}, '%')
and s.status = #{status}
order by id desc
