mapper.select:
select s.id, s.category_id, s.name, s.price, s.status, s.description, s.image, s.update_time, c.name from setmeal as s left join category as c on s.category_id = c.id
and s.category_id = #{categoryId}
and s.name like concat('%', #{name}, '%')
and s.status = #{status}
order by id desc

mapper.update:
update sky_take_out.setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}
